SELECT @typePlan = typeid FROM gemini_issuetypes WHERE typedesc='Test Plan' AND templateid=@templateid
SELECT @typeCase = typeid FROM gemini_issuetypes WHERE typedesc='Test Case' AND templateid=@templateid
SELECT @expectedResult = customfieldid FROM gemini_customfielddefinitions WHERE customfieldname='Expected Results' AND templateid=@templateid
SELECT @preConditions = customfieldid FROM gemini_customfielddefinitions WHERE customfieldname='Preconditions' AND templateid=@templateid
SELECT @type = customfieldid FROM gemini_customfielddefinitions WHERE customfieldname='Test Type' AND templateid=@templateid
SELECT @defStatus = MIN(statusid) FROM gemini_issuestatus WHERE templateid=@templateid
SELECT @defPriority = MIN(priorityid) FROM gemini_issuepriorities WHERE templateid=@templateid
SELECT @defSeverity = MIN(severityid) FROM gemini_issueseverity WHERE templateid=@templateid
SELECT @defResolution = MIN(resolutionid) FROM gemini_issueresolutions WHERE templateid=@templateid

-- Migrate Plans to issues table
INSERT INTO gemini_issues(created, issuepriorityid, issueresolutionid, issueseverityid, issuestatusid, issuetypeid, longdesc, projectid, reportedby, summary, userdata1, userdata2, visibility)
SELECT created, @defPriority, @defResolution, @defSeverity, @defStatus, @typePlan, plandescription, projectid, createdby, summary, testplanid, 'Migrated Plan', 1  FROM gemini_testing_plans WHERE isfolder=0

-- Migrate Cases to issues table
INSERT INTO gemini_issues(created, issuepriorityid, issueresolutionid, issueseverityid, issuestatusid, issuetypeid, longdesc, projectid, reportedby, summary, userdata1, userdata2, visibility, resources, revised)
SELECT created, @defPriority, @defResolution, @defSeverity, @defStatus, @typeCase, testcasedescription , projectid, createdby, summary, testcaseid, 'Migrated Case', 1,  CASE WHEN assignedto IS NULL THEN '' ELSE '|' + CAST(assignedto AS VARCHAR(10)) + '|' END, revised FROM gemini_testing_cases

INSERT INTO gemini_issueresources(issueid, userid)
SELECT i.issueid, c.assignedto FROM gemini_testing_cases c JOIN gemini_issues i ON i.userdata2='Migrated Case' AND i.userdata1=c.testcaseid WHERE c.assignedto IS NOT NULL
-- Move Case associate data to custom fields
INSERT INTO gemini_customfielddata(customfieldid, fielddata, issueid, projectid, userid)
SELECT @expectedResult, c.expectedresult, i.issueid, i.projectid, c.createdby FROM gemini_testing_cases c JOIN gemini_issues i ON i.userdata2='Migrated Case' AND i.userdata1=c.testcaseid
INSERT INTO gemini_customfielddata(customfieldid, fielddata, issueid, projectid, userid)
SELECT @preConditions, c.preconditions, i.issueid, i.projectid, c.createdby FROM gemini_testing_cases c JOIN gemini_issues i ON i.userdata2='Migrated Case' AND i.userdata1=c.testcaseid
INSERT INTO gemini_customfielddata(customfieldid, fielddata, issueid, projectid, userid)
SELECT @type, c.typeid, i.issueid, i.projectid, c.createdby FROM gemini_testing_cases c JOIN gemini_issues i ON i.userdata2='Migrated Case' AND i.userdata1=c.testcaseid

